mapper.select:
SELECT r.recordId, r.patientName, r.phone, r.age, r.description, r.status, r.score, r.createTime, r.updateTime, d.doctorId, d.doctorName, d.departmentName, d.hospitalName FROM record r LEFT JOIN doctor d ON r.doctorId = d.doctorId
AND r.status IN
#{item}
AND r.patientId = #{patientId}
AND d.doctorId = #{doctorId}
ORDER BY r.createTime DESC LIMIT #{offset}, #{pageSize}

SELECT COUNT(*)
FROM record r
LEFT JOIN doctor d ON r.doctorId = d.doctorId


        AND r.status IN

            #{status}



        AND r.patientId = #{patientId}


        AND r.patientName LIKE CONCAT('%', #{patientName}, '%')


        AND d.doctorName LIKE CONCAT('%', #{doctorName}, '%')


        AND d.doctorId = #{doctorId}
